var scores = [
  { _id: 1, point: 0 },
  { _id: 2, point: 0 },
  { _id: 3, point: 0 },
  { _id: 4, point: 0 },
  { _id: 5, point: 0 },
  { _id: 6, point: 0 },
  { _id: 7, point: 0 },
  { _id: 8, point: 0 },
  { _id: 9, point: 0 },
  { _id: 10, point: 0 },
];

export function saveScore(id, selectedOption, answer) {
  let scoreInDb = scores.findIndex((x) => x._id === id);
  if (parseInt(selectedOption) === parseInt(answer.oId)) {
    scores[scoreInDb].point = scores[scoreInDb].point + 1;
  } else if (selectedOption !== answer.oId) {
    scores[scoreInDb].point = 0;
  }
  return scores[scoreInDb].point;
}

export function getScore() {
  let sum = 0;
  for (var i in scores) {
    if (scores[i].point === 1) sum = sum + 1;
  }
  return sum;
}
