import React, { Component } from "react";
import { getQustions, getQuestion } from "./getQuestions";
import { getAnswer } from "./getAnswer";
class QuestionLayout extends Component {
  state = {
    id: 1,
    question: getQuestion(1),
    answer: getAnswer(1),
  };

  handleButton = () => {
    if (this.state.id > 0 && this.state.id < 11)
      return (
        <button
          type="button"
          className="btn btn-primary"
          onClick={this.handleNext}
        >
          Next
        </button>
      );
    else if (this.state.id === 10)
      return (
        <button type="button" className="btn btn-primary">
          Submit
        </button>
      );
  };
  handlePrevious = () => {
    let id = this.state.id;
    id = id - 1;
    this.setState({ id: id, question: getQuestion(id), answer: getAnswer(id) });
  };
  handleNext = () => {
    let id = this.state.id;
    id = id + 1;
    this.setState({ id: id, question: getQuestion(id), answer: getAnswer(id) });
  };
  render() {
    return (
      <div className="container p-5">
        <div>
          <h1>{this.state.question.title}</h1>
        </div>
        <div className="custom-control custom-radio">
          {this.state.question.options.map((m) => (
            <div>
              <input
                type="radio"
                id={m._id}
                name="customRadio"
                className="custom-control-input"
              />
              <label className="custom-control-label" htmlFor={m._id}>
                {m.option}
              </label>
            </div>
          ))}
        </div>
        <div>
          <button
            type="button"
            className="btn btn-primary"
            onClick={this.handlePrevious}
          >
            Previous
          </button>
          {this.handleButton}
        </div>
      </div>
    );
  }
}

export default QuestionLayout;
