import React, { Component } from "react";
import { getQuestion } from "./getQuestions";
import { getAnswer } from "./getAnswer";
import Button from "./button";
import { getScore } from "./getScore";
class QuestionLayout extends Component {
  state = {
    id: 1,
    question: getQuestion(1),
    answer: getAnswer(1),
    score: 0,
    selectedOption: "",
  };

  handleOptionChange = async (changeEvent) => {
    this.setState(
      {
        selectedOption: changeEvent.target.value,
      },
      this.changeScore
    );
  };

  handleNext = () => {
    let id = this.state.id;
    id = id + 1;
    this.setState({ id: id, question: getQuestion(id), answer: getAnswer(id) });
  };
  changeScore = () => {
    let score = this.state.score;
    score = score + 1;
    if (this.state.selectedOption === this.state.answer.oId) {
      this.setState({ score: score });
    }
    if (this.state.id === 10) getScore(this.state.score);
  };
  render() {
    return (
      <div className="container p-5">
        <div>
          <h1>{this.state.question.title}</h1>
        </div>
        <div className="custom-control custom-radio">
          {this.state.question.options.map((m) => (
            <div key={m._id}>
              <input
                type="radio"
                id={m._id}
                name="customRadio"
                value={m._id}
                onChange={this.handleOptionChange}
                className="custom-control-input"
              />
              <label className="custom-control-label" htmlFor={m._id}>
                {m._id} {m.option}
              </label>
            </div>
          ))}
        </div>

        <div>
          <Button
            id={this.state.id}
            onClickPrevious={this.handlePrevious}
            onClickNext={this.handleNext}
          />
        </div>
        <p>{this.state.score}</p>
      </div>
    );
  }
}

export default QuestionLayout;
